import logo from './logo.svg';
import './App.css';
import scissors from './images/icon-scissors.svg'
import paper from './images/icon-paper.svg'
import rock from './images/icon-rock.svg'
import {useState} from 'react';

function App() {
  const [img1, setImg1] = useState(scissors)
  const [img2, setImg2] = useState(scissors)
  const [result1, setResult1] = useState()

  let rps = ['rock', 'paper', 'scissors']

  function handleChange() {
    let randomNumber = Math.floor(Math.random() * 3)
    // let result = rps[randomNumber]
    if(rps[randomNumber] == 'rock') {
        setImg2(rock)
    }else if (rps[randomNumber] == 'paper') {
      setImg2(paper)
    }else{
      setImg2(scissors)
    }
  }

  return (
    <div className="App">
       <div className='Container'>
        <div>
          <h1>YOU PICKED</h1>
          <img src={img1} alt="" />
          <h2></h2>
        </div>
        <div>
          <button onClick={() => {
            handleChange()
          }}>PLAY AGAIN</button>
        </div>
        <div>
          <h1>THE HOUSE PICKED</h1>
          <img src={img2} alt="" />
          <h2></h2>
        </div>
       </div>
       <div className='Btns'>
       <button>STARTOVER</button>
       <div>
        <button onClick={() => setImg1(rock)}>ROCK</button>
        <button onClick={() => setImg1(paper)}>PAPER</button>
        <button onClick={() => setImg1(scissors) }>SCISSORS</button>
       </div>
       </div>
    </div>
  );
}

export default App;
