import logo from './logo.svg';
import './App.css';
import scissors from './images/icon-scissors.svg'
import paper from './images/icon-paper.svg'
import rock from './images/icon-rock.svg'
import {useState} from 'react';

function App() {
  const [img1, setImg1] = useState(scissors)
  const [img2, setImg2] = useState(scissors)
  const [result1, setResult1] = useState('SCISSORS')
  const [result2, setResult2] = useState('SCISSORS')
  const [score, setScore] = useState(0)
  const [scores, setScores] = useState(0)
  const [hasWon, setHasWon] = useState('YOU LOSE')

  let rps = ['rock', 'paper', 'scissors']

  function handleChange() {
    let randomNumber = Math.floor(Math.random() * 3)
    let result = rps[randomNumber]
    let result1 = rps[randomNumber]
    if(rps[randomNumber] == 'rock') {
        setImg2(rock)
        setResult1('ROCK')
    }else if (rps[randomNumber] == 'paper') {
      setImg2(paper)
      setResult1('PAPER')
    }else{
      setImg2(scissors)
      setResult1('SCISSORS')
    }
    if (result == result1) {
      setHasWon('IT IS A TIE')
    }else if (result == 'rock' && result1 == 'scissors'){
        setHasWon('YOU WIN')
        setScore(score + 1)
    }else if (result == 'scissors' && result1 == 'paper') {
      setHasWon('YOU WIN')
      setScore(score + 1)
    }else if (result == 'paper' && result1 == 'rock') {
      setHasWon('YOU WIN')
      setScore(score + 1)
    }else {
      setHasWon('YOU LOSE')
      setScores(scores + 1)
    }
  }
  function handleChange1() {
    setImg1(rock)
    setResult2('ROCK')
  }
  function handleChange2() {
    setImg1(paper)
    setResult2('PAPER')
  }
  function handleChange3() {
    setImg1(scissors)
    setResult2('SCISSORS')
  }

  return (
    <div className="App">
       <div className='scor'>
      <h1 className='rock'>ROCK PAPER SCISSORS</h1>
        <div className='display'>
      <h1><span className='user'>Your Score:</span> {score}</h1>
      <h1><span className='com'>Computer Score:</span> {scores}</h1>
      </div>
      </div>
       <div className='Container'>  
        <div>
          <h1>YOU PICKED</h1>
          <img src={img1} alt="" />
          <h2>{result2}</h2>
        </div>
        <div>
          h2
          <button onClick={() => {
            handleChange()
          }}>PLAY</button>
        </div>
        <div>
          <h1>THE HOUSE PICKED</h1>
          <img src={img2} alt="" />
          <h2>{result1}</h2>
          <h2></h2>
        </div>
       </div>
       <div className='Btns'>
       <button>STARTOVER</button>
       <div>
        <button onClick={() => {
          handleChange1()
        }}>ROCK</button>
        <button onClick={() => {
          handleChange2()
        }}>PAPER</button>
        <button onClick={() => {
          handleChange3()
        }}>SCISSORS</button>
       </div>
       </div>
    </div>
  );
}

export default App;
